sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below

	if (newValue != "") {
		var ga = new GlideAjax("DeviceRequestUtils");
		ga.addParam("sysparm_name", "getDeviceDetails");
		ga.addParam("sysparm_id", g_form.getValue("device_name"));
		ga.getXMLAnswer(setDeviceDetails);

		g_form.setSectionDisplay("device_details", true);
		g_form.setSectionDisplay("delivery_details", true);
	}

}

function setDeviceDetails(response) {
	var answer = JSON.parse(response);
	g_form.setValue("model", answer.model);
	g_form.setValue("price", answer.price);
	g_form.setValue("device_description", answer.deviceDesc);
	g_form.setValue("warranty_expiration", answer.warrantyExp);
}
