sys_script_include.script:
var DeviceRequestUtils = Class.create();
DeviceRequestUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getDeviceDetails: function () {
		const deviceID = this.getParameter("sysparm_id");

		try {
			const gr = new GlideRecord("x_1531514_dispatch_ai_devices");
			gr.addQuery("sys_id", deviceID);
			gr.query();

			if (gr.next()) {
				const deviceDetails = {
					model: gr.getValue("model_number"),
					price: gr.getValue("price"),
					deviceDesc: gr.getValue("short_description"),
					warrantyExp: gr.getValue("warranty_expiration")
				};

				return JSON.stringify(deviceDetails);
			}
		} catch (err) {
			gs.error("ERROR || DeviceRequestUtils || " + err);
		}
	},

	type: 'DeviceRequestUtils'
});
